######## Convolution with Laplacian Filter ##################

import numpy as np
import matplotlib.pyplot as plt

# Define the Laplacian kernel
laplacian_kernel = np.array([[0, 1, 0],
                              [1, -4, 1],
                              [0, 1, 0]])

def convolution(image, kernel):
    image_height, image_width = image.shape
    kernel_height, kernel_width = kernel.shape
    result_height = image_height - kernel_height + 1
    result_width = image_width - kernel_width + 1
    result = np.zeros((result_height, result_width))

    for i in range(result_height):
        for j in range(result_width):
            region = image[i:i+kernel_height, j:j+kernel_width]
            convolution_result = np.sum(region * kernel)
            result[i, j] = convolution_result

    return result

convolution_result = convolution(img_array, laplacian_kernel)

print("Convolution result for first ten rows:")
print(convolution_result[:10, :])

print("Shape of the convolution result:", convolution_result.shape)

plt.imshow(convolution_result, cmap='viridis')
plt.axis('off')
plt.title('Convolution Result')
plt.show()

print("Validation for first 5x5 array:")
print(convolution_result[0:5, 0:5])

