######## Convolution with Laplacian Filter and the setting of stride=2 ##################

import numpy as np
import matplotlib.pyplot as plt

laplacian_kernel = np.array([[0, 1, 0],
                              [1, -4, 1],
                              [0, 1, 0]])

def convolution_with_stride(image, kernel, stride=1):
    image_height, image_width = image.shape
    kernel_height, kernel_width = kernel.shape
    result_height = (image_height - kernel_height) // stride + 1
    result_width = (image_width - kernel_width) // stride + 1
    result = np.zeros((result_height, result_width))

    for i in range(0, image_height - kernel_height + 1, stride):
        for j in range(0, image_width - kernel_width + 1, stride):
            region = image[i:i+kernel_height, j:j+kernel_width]
            convolution_result = np.sum(region * kernel)
            result[i//stride, j//stride] = convolution_result

    return result

convolution_result_stride2 = convolution_with_stride(img_array, laplacian_kernel, stride=2)

print("Convolution result with stride=2 for first ten rows:")
print(convolution_result_stride2[:10, :])

print("Shape of the convolution result with stride=2:", convolution_result_stride2.shape)

